

document.addEventListener('DOMContentLoaded', function() {
  
    const url = 'https://www.thecocktaildb.com/api/json/v1/1/filter.php?c=Coffee / Tea';
    const itemsPerPage = 6;
    let currentPage = 1;
    
    fetch(url)
      .then(response => response.json())
      .then(data => {
        const drinkList = document.getElementById('drinkList');
        const pagination = document.querySelector('#pagination ul');
        
        // Función para mostrar las imágenes según la página actual
        function showImages(page) {
          
          drinkList.innerHTML = '';
  
          const startIndex = (page - 1) * itemsPerPage;
          const endIndex = startIndex + itemsPerPage;
          const drinksToShow = data.drinks.slice(startIndex, endIndex);
  
          drinksToShow.forEach(drink => {
            const li = document.createElement('li');
            li.classList.add('col-md-6', 'col-lg-3','my-4', 'row', 'align-items-center', 'p-3', 'mb-4');
  
            const img = document.createElement('img');
            img.src = drink.strDrinkThumb;
            img.alt = drink.strDrink;
            img.classList.add('img-fluid', 'border', 'shadow-sm', 'p-2', 'rounded', 'scale-on-hover');
            li.appendChild(img);
            console.log(data)
            const h3 = document.createElement('h3');
            h3.classList.add('text-black', 'fs-5', 'fw-bold', 'text-uppercase', 'text-center','mt-3', 'rounded','btn','btn-primary','no-cursor-pointer');
            h3.textContent = drink.strDrink;
            li.appendChild(h3);
  
            drinkList.appendChild(li);
          });
          
        }
  
        // Función para generar los elementos del paginador
        function generatePagination() {
          
          pagination.innerHTML = '';
  
          const totalPages = Math.ceil(data.drinks.length / itemsPerPage);
  
          for (let i = 1; i <= totalPages; i++) {
            const li = document.createElement('li');
            li.classList.add('page-item');
  
            const link = document.createElement('a');
            link.href = '#';
            link.textContent = i;
            link.classList.add('page-link','text-black');
            li.appendChild(link);
  
            if (i === currentPage) {
              li.classList.add('active');
            }
  
            pagination.appendChild(li);
          }
        }
  
        // Mostrar las imágenes y generar el paginador
        showImages(currentPage);
        generatePagination();
  
        // Event listener para cambiar de página al hacer clic en un número del paginador
        pagination.addEventListener('click', function(event) {
          if (event.target.tagName === 'A') {
            event.preventDefault();
            currentPage = parseInt(event.target.textContent);
            showImages(currentPage);
            generatePagination();
          }
        });
      })
      .catch(error => {
        console.error('Error:', error);
      });
  });
  
  